import React from "react";
import { PieChart, Pie, Sector, Cell } from "recharts";
import { useIncomeContext } from '../hooks/useIncomeContext';

const IncomePieCharts = () => {
    const { income } = useIncomeContext();

    // Extract names and amounts from income object
    const data = Object.entries(income).map(([category, amount]) => ({
        name: category,
        value: amount,
    }));

    // Example data structure:
    // const data = [
    //     { name: 'Salary', value: 3000 },
    //     { name: 'Freelance', value: 2000 },
    //     { name: 'Investments', value: 1500 },
    //     ...
    // ];

    return (
        <PieChart width={400} height={400}>
            <Pie
                dataKey="value"
                isAnimationActive={false}
                data={data}
                cx={200}
                cy={200}
                outerRadius={80}
                fill="#8884d8"
                label
            >
                {data.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={`#${Math.floor(Math.random() * 16777215).toString(16)}`} />
                ))}
            </Pie>
        </PieChart>
    );
};

export default IncomePieCharts;
