import { useEffect } from 'react';
import { useExpensesContext } from "../hooks/useExpensesContext";
import { useAuthContext } from '../hooks/useAuthContext';
import ExpenseDetails from '../components/ExpenseDetails';
import ExpenseForm from '../components/ExpenseForm';
import IncomeDetails from '../components/IncomeDetails';
import IncomeForm from '../components/IncomeForm';
import { useIncomeContext } from '../hooks/useIncomeContext';

const Home = () => {
    const { expenses, dispatch } = useExpensesContext();
    const { income, dispatch: incomeDispatch } = useIncomeContext();
    const { user } = useAuthContext();

    useEffect(() => {
        const fetchData = async () => {
            if (!user) return;

            try {
                const expenseResponse = await fetch('/api/expenses', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const expenseData = await expenseResponse.json();
                if (expenseResponse.ok) {
                    dispatch({ type: 'SET_EXPENSES', payload: expenseData });
                }

                const incomeResponse = await fetch('/api/income', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const incomeData = await incomeResponse.json();
                if (incomeResponse.ok) {
                    incomeDispatch({ type: 'SET_INCOME', payload: incomeData });
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, [dispatch, incomeDispatch, user]);

    return (
        <div className="home">
            {user && (
                <>
                    <div className="expenses">
                        {expenses && expenses.map((expense) => (
                            <ExpenseDetails key={expense._id} expense={expense} />
                        ))}
                    </div>
                    <ExpenseForm />

                    <div className="incomes">
                        {income && income.map((incomeItem) => (
                            <IncomeDetails key={incomeItem._id} income={incomeItem} />
                        ))}
                    </div>
                    <IncomeForm />
                </>
            )}
        </div>
    );
};

export default Home;
