import React from "react";
import { PieChart, Pie, Cell } from "recharts";
import { useIncomeContext } from '../hooks/useIncomeContext';
import { useExpensesContext } from '../hooks/useExpensesContext';

const CompletePieChart = () => {
    const { expenses } = useExpensesContext();
    const { income } = useIncomeContext();

    // Calculate total income
    const totalIncome = income.reduce((acc, curr) => acc + curr.amount, 0);

    // Calculate total expenses
    const totalExpenses = expenses.reduce((acc, curr) => acc + curr.amount, 0);

    // Check if both total income and total expenses are 0
    if (totalIncome === 0 && totalExpenses === 0) {
        // Return balanced pie chart
        return (
            <div className="CompletePieChart">
                <h3 style={{ marginBottom: "-75px" }}>Expenses vs Income</h3>
                <div style={{ display: "flex", justifyContent: "center" }}>
                    <PieChart width={400} height={400}>
                        <Pie
                            dataKey="value"
                            data={[{ name: "Balanced", value: 1 }]}
                            cx={200}
                            cy={200}
                            innerRadius={60}
                            outerRadius={100}
                            fill="#8884d8"
                            label
                        >
                            <Cell fill="#8884d8" />
                        </Pie>
                    </PieChart>
                </div>
            </div>
        );
    }

    const data = [
        { name: "Expense", value: totalExpenses },
        { name: "Income", value: totalIncome },
    ];

    const colors = ['#ff6961', '#77dd77']; // Red for expenses, green for income

    return (
        <div className="CompletePieChart">
            <h3 style={{ marginBottom: "-75px" }}>Expenses vs Income</h3>
            <div style={{ display: "flex", justifyContent: "center" }}>
                <PieChart width={400} height={400}>
                    <Pie
                        dataKey="value"
                        data={data}
                        cx={200}
                        cy={200}
                        innerRadius={60}
                        outerRadius={100}
                        fill="#8884d8"
                        label
                    >
                        {data.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={colors[index]} />
                        ))}
                    </Pie>
                </PieChart>
            </div>
        </div>
    );
};

export default CompletePieChart;
